import React from 'react';
import { FaUserGraduate } from 'react-icons/fa';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

export default function StudentDashboard({ name, id }) {
  const navigate = useNavigate();

  const handleViewGrades = () => {
    navigate('/grades');
  };

  return (
    <div style={styles.wrapper}>
      <motion.div
        style={styles.card}
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <motion.div
          style={styles.iconWrapper}
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5, type: 'spring', stiffness: 150 }}
        >
          <FaUserGraduate style={styles.icon} />
        </motion.div>
        <motion.h1
          style={styles.title}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4, duration: 0.6 }}
        >
          Hoşgeldiniz, {name}!
        </motion.h1>
        <motion.p
          style={styles.subtitle}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.6 }}
        >
          Öğrenci Numaranız: <strong>{id}</strong>
        </motion.p>
        <motion.p
          style={styles.info}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.8, duration: 0.6 }}
        >
          Lütfen yukarıdaki menüden notlarınıza, derslerinize veya profil bilgilerinize ulaşabilirsiniz.
        </motion.p>
        
        <motion.button
          style={styles.button}
          onClick={handleViewGrades}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          transition={{ type: 'spring', stiffness: 300 }}
        >
          Notlarımı Görüntüle
        </motion.button>
      </motion.div>
    </div>
  );
}

const styles = {
  wrapper: {
    minHeight: '100vh',
    background: 'linear-gradient(to right, #6a11cb, #2575fc)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '40px',
  },
  card: {
    backgroundColor: '#fff',
    padding: '40px',
    borderRadius: '20px',
    boxShadow: '0 12px 24px rgba(0, 0, 0, 0.2)',
    textAlign: 'center',
    maxWidth: '600px',
    width: '100%',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    position: 'relative',
  },
  iconWrapper: {
    background: '#2575fc',
    width: '80px',
    height: '80px',
    borderRadius: '50%',
    marginBottom: '20px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    fontSize: '40px',
    color: '#fff',
  },
  title: {
    fontSize: '28px',
    marginBottom: '10px',
    color: '#333',
  },
  subtitle: {
    fontSize: '18px',
    color: '#666',
    marginBottom: '15px',
  },
  info: {
    fontSize: '16px',
    color: '#444',
    marginBottom: '25px',
  },
  button: {
    padding: '12px 24px',
    fontSize: '16px',
    backgroundColor: '#2575fc',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    fontWeight: 'bold',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.2)',
  },
};
