import React, { useEffect, useState } from 'react';

export default function StudentAnnouncementsPage({ studentId }) {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/messages/student/${studentId}`)
      .then(res => {
        if (!res.ok) throw new Error('Veri alınamadı');
        return res.json();
      })
      .then(data => {
        setMessages(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [studentId]);

  if (loading) return <p>Yükleniyor...</p>;
  if (error) return <p style={{ color: 'red' }}>Hata: {error}</p>;

  return (
    <div style={{ maxWidth: 700, margin: '30px auto', padding: 20, fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h2>Duyurular ve Mesajlar</h2>
      {messages.length === 0 ? (
        <p>Gösterilecek mesaj veya duyuru yok.</p>
      ) : (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {messages.map(msg => (
            <li key={msg.id} style={{
              marginBottom: 15,
              padding: 15,
              borderRadius: 6,
              backgroundColor: msg.receiverAll ? '#e3f2fd' : '#f1f8e9',
              border: '1px solid',
              borderColor: msg.receiverAll ? '#90caf9' : '#aed581'
            }}>
              <strong>{msg.receiverAll ? 'Duyuru' : 'Mesaj'}</strong><br />
              <em>Gönderen: {msg.teacher?.username || 'Bilinmiyor'}</em>
              <p style={{ marginTop: 8 }}>{msg.content}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
