import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';

export default function TeacherRegister() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(false);
  const location = useLocation();

  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => setMessage(''), 4000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  const handleRegister = async (e) => {
    e.preventDefault();

    if (username.trim().length < 3) {
      setMessage('Kullanıcı adı en az 3 karakter olmalı.');
      setIsError(true);
      return;
    }
    if (password.trim().length < 6) {
      setMessage('Şifre en az 6 karakter olmalı.');
      setIsError(true);
      return;
    }

    setLoading(true);
    setMessage('');
    setIsError(false);

    try {
      const res = await axios.post('http://localhost:8080/api/auth/teacher/register', {
        username: username.trim(),
        password: password.trim(),
      });
      setMessage(res.data || '✅ Kayıt başarılı! Giriş yapabilirsiniz.');
      setIsError(false);
      setUsername('');
      setPassword('');
    } catch (err) {
      setMessage(err.response?.data?.message || '❌ Bir hata oluştu. Tekrar deneyin.');
      setIsError(true);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.wrapper}>
      <nav style={styles.nav}>
        <Link
          to="/student-login"
          style={{
            ...styles.navButton,
            ...(location.pathname === '/student-login' ? styles.navButtonActive : {}),
          }}
        >
          Öğrenci Giriş
        </Link>
        <Link
          to="/teacher-login"
          style={{
            ...styles.navButton,
            ...(location.pathname === '/teacher-login' ? styles.navButtonActive : {}),
          }}
        >
          Öğretmen Giriş
        </Link>
        <Link
          to="/teacher-register"
          style={{
            ...styles.navButton,
            ...(location.pathname === '/teacher-register' ? styles.navButtonActive : {}),
          }}
        >
          Öğretmen Kayıt
        </Link>
      </nav>

      <form style={styles.form} onSubmit={handleRegister} noValidate>
        <h2 style={styles.title}>Öğretmen Kayıt</h2>

        <div style={styles.inputGroup}>
          <input
            id="username"
            type="text"
            placeholder="Kullanıcı Adınızı Girin"
            value={username}
            onChange={e => setUsername(e.target.value)}
            disabled={loading}
            style={{
              ...styles.input,
              borderColor: username && username.trim().length < 3 && isError ? '#e74c3c' : '#ccc',
            }}
            autoComplete="username"
            required
            minLength={3}
          />
        </div>

        <div style={styles.inputGroup}>
          <input
            id="password"
            type="password"
            placeholder="Şifrenizi Girin"
            value={password}
            onChange={e => setPassword(e.target.value)}
            disabled={loading}
            style={{
              ...styles.input,
              borderColor: password && password.trim().length < 6 && isError ? '#e74c3c' : '#ccc',
            }}
            autoComplete="new-password"
            required
            minLength={6}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={loading ? styles.buttonDisabled : styles.button}
        >
          {loading ? 'Kayıt Oluyor...' : 'Kayıt Ol'}
        </button>

        {message && (
          <p
            style={{
              ...styles.message,
              color: isError ? '#e74c3c' : '#2ecc71',
              opacity: message ? 1 : 0,
              transition: 'opacity 0.5s ease',
              marginTop: 20,
              fontWeight: '600',
              textAlign: 'center',
            }}
            role="alert"
          >
            {message}
          </p>
        )}
      </form>
    </div>
  );
}

const styles = {
  wrapper: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #89f7fe, #66a6ff)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  nav: {
    display: 'flex',
    gap: 20,
    marginBottom: 30,
  },
  navButton: {
    padding: '10px 25px',
    borderRadius: 30,
    textDecoration: 'none',
    color: '#1e3c72',
    fontWeight: '700',
    fontSize: '1rem',
    backgroundColor: '#e0e7ff',
    boxShadow: '0 4px 8px rgba(30,60,114,0.2)',
    userSelect: 'none',
    transition: 'all 0.3s ease',
  },
  navButtonActive: {
    backgroundColor: '#1e3c72',
    color: '#fff',
    boxShadow: '0 6px 12px rgba(30,60,114,0.6)',
  },
  form: {
    backgroundColor: '#fff',
    borderRadius: 16,
    padding: '40px 30px',
    boxShadow: '0 15px 35px rgba(0,0,0,0.2)',
    width: '100%',
    maxWidth: 420,
    boxSizing: 'border-box',
  },
  title: {
    textAlign: 'center',
    marginBottom: 30,
    fontSize: '2rem',
    color: '#333',
    fontWeight: '700',
    letterSpacing: '1.5px',
  },
  inputGroup: {
    marginBottom: 20,
  },
  input: {
    width: '100%',
    padding: '14px 18px',
    fontSize: '1rem',
    borderRadius: 8,
    border: '1.8px solid #ccc',
    outline: 'none',
    boxSizing: 'border-box',
    transition: 'border-color 0.3s ease',
  },
  button: {
    marginTop: 10,
    width: '100%',
    padding: '14px',
    fontSize: '1.15rem',
    backgroundColor: '#4a90e2',
    color: 'white',
    fontWeight: '700',
    border: 'none',
    borderRadius: 12,
    cursor: 'pointer',
    boxShadow: '0 8px 15px rgba(74,144,226,0.3)',
    transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
  },
  buttonDisabled: {
    marginTop: 10,
    width: '100%',
    padding: '14px',
    fontSize: '1.15rem',
    backgroundColor: '#a0bce6',
    color: '#fff',
    fontWeight: '700',
    border: 'none',
    borderRadius: 12,
    cursor: 'not-allowed',
  },
  message: {
    fontSize: '1.1rem',
  },
};
