import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from '../components/TeacherHomePage/TeacherHomePage.module.css';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const teacherRes = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(teacherRes.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  const handleAddStudent = () => navigate('/ogrenci-ekle');
  const handleShowStudents = () => navigate('/ogrenci-listesi');
  const handleViewMessages = () => navigate('/ogretmen-mesajlar');
  const handleSendMessage = () => navigate('/teacher/send-message', { state: { teacher } });
  const handleSendAnnouncement = () => navigate('/teacher/send-announcement', { state: { teacher } });
  const handleLogout = () => navigate('/teacher-login');

  if (loading) return <div className={styles.loading}>Yükleniyor...</div>;
  if (!teacher) return <div className={styles.error}>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h1>Okul Yönetim Sistemi</h1>
        <h2>Hoşgeldiniz, {teacher.name}!</h2>
        <p className={styles.welcomeMessage}>
          Bugün sizin için hazırlanan araçlarla öğrencilerinizle iletişimde kalabilir, notları takip edebilir ve duyurularınızı kolayca yapabilirsiniz.
        </p>
      </header>

      <section className={styles.infoCards}>
        <div className={styles.card}>
          <h3>Öğrenci Yönetimi</h3>
          <p>Öğrenci listesine göz atabilir, yeni öğrenciler ekleyebilirsiniz.</p>
          <div className={styles.cardButtons}>
            <button className={`${styles.button} ${styles.blue}`} onClick={handleShowStudents}>Öğrenci Listesi</button>
            <button className={`${styles.button} ${styles.green}`} onClick={handleAddStudent}>Öğrenci Ekle</button>
          </div>
        </div>

        <div className={styles.card}>
          <h3>Mesajlar & Duyurular</h3>
          <p>Öğrencilere mesaj gönderip gelen mesajları takip edin, duyurularınızı yayınlayın.</p>
          <div className={styles.cardButtons}>
            <button className={`${styles.button} ${styles.orange}`} onClick={handleSendMessage}>Mesaj Gönder</button>
            <button className={`${styles.button} ${styles.purple}`} onClick={handleSendAnnouncement}>Duyuru Yap</button>
            <button className={`${styles.button} ${styles.redOrange}`} onClick={handleViewMessages}>Mesajları Görüntüle</button>
          </div>
        </div>
      </section>

      <footer className={styles.footer}>
        <button className={`${styles.button} ${styles.red}`} onClick={handleLogout}>Çıkış Yap</button>
      </footer>
    </div>
  );
};

export default TeacherHomePage;
