import React from 'react';
import { FaUserGraduate } from 'react-icons/fa';
import { motion } from 'framer-motion';

export default function StudentDashboard({ name, id }) {
  const handleViewGrades = () => {
    alert("Notlar sayfasına yönlendiriliyorsunuz..."); 
    // Alternatif: navigate('/grades') - eğer React Router kullanıyorsan
  };

  return (
    <div style={styles.wrapper}>
      <motion.div
        style={styles.card}
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <FaUserGraduate style={styles.icon} />
        <h1 style={styles.title}>Hoşgeldiniz, {name}!</h1>
        <p style={styles.subtitle}>Öğrenci Numaranız: <strong>{id}</strong></p>
        <p style={styles.info}>Lütfen yukarıdaki menüden notlarınıza, derslerinize veya profil bilgilerinize ulaşabilirsiniz.</p>
        
        <button style={styles.button} onClick={handleViewGrades}>
          Notlarımı Görüntüle
        </button>
      </motion.div>
    </div>
  );
}

const styles = {
  wrapper: {
    minHeight: '100vh',
    background: 'linear-gradient(to right, #6a11cb, #2575fc)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '20px',
  },
  card: {
    backgroundColor: '#fff',
    padding: '40px',
    borderRadius: '20px',
    boxShadow: '0 12px 24px rgba(0, 0, 0, 0.2)',
    textAlign: 'center',
    maxWidth: '500px',
    width: '100%',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  icon: {
    fontSize: '50px',
    color: '#2575fc',
    marginBottom: '20px',
  },
  title: {
    fontSize: '28px',
    marginBottom: '10px',
    color: '#333',
  },
  subtitle: {
    fontSize: '18px',
    color: '#666',
    marginBottom: '15px',
  },
  info: {
    fontSize: '16px',
    color: '#444',
    marginBottom: '25px',
  },
  button: {
    padding: '12px 24px',
    fontSize: '16px',
    backgroundColor: '#2575fc',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
};

styles.button[':hover'] = {
  backgroundColor: '#1e5ee8',
};
