import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const teacherRes = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(teacherRes.data);

        const studentsRes = await axios.get('http://localhost:8080/api/students');
        setStudents(studentsRes.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  const handleAddStudent = () => {
    navigate('/ogrenci-ekle');
  };

  const handleLogout = () => {
    navigate('/teacher-login');
  };

  if (loading) return <div>Yükleniyor...</div>;
  if (!teacher) return <div>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Hoşgeldiniz, {teacher.name}</h2>
        <div>
          <button
            onClick={handleAddStudent}
            style={{
              padding: '8px 16px',
              marginRight: 10,
              backgroundColor: '#4CAF50',
              color: '#fff',
              border: 'none',
              borderRadius: 5,
            }}
          >
            Öğrenci Ekle
          </button>
          <button
            onClick={handleLogout}
            style={{
              padding: '8px 16px',
              backgroundColor: '#f44336',
              color: 'white',
              border: 'none',
              borderRadius: 5,
            }}
          >
            Çıkış
          </button>
        </div>
      </div>

      <h3 style={{ marginTop: 30 }}>Öğrenci Listesi</h3>
      {students.length === 0 ? (
        <p>Kayıtlı öğrenci yok.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', marginTop: 10 }}>
          <thead>
            <tr>
              <th>Öğrenci No</th>
              <th>Ad</th>
              <th>Soyad</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.id}>
                <td>{student.studentNumber || student.id}</td>
                <td>{student.name}</td>
                <td>{student.surname}</td>
                <td>{student.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default TeacherHomePage;
