import React, { useState } from 'react';
import axios from 'axios';

export default function TeacherLogin({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    try {
      const res = await axios.post('http://localhost:8080/api/auth/teacher/login', { username, password });
      setMessage('✅ Giriş başarılı! Hoşgeldiniz ' + res.data.username);
      setUsername('');
      setPassword('');
      if (onLoginSuccess) {
        onLoginSuccess(res.data.username);
      }
    } catch (err) {
      const errorData = err.response?.data;
      if (errorData && typeof errorData === 'object') {
        setMessage(errorData.message || JSON.stringify(errorData));
      } else {
        setMessage(errorData || '❌ Giriş başarısız.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.wrapper}>
      <form style={styles.form} onSubmit={handleLogin} noValidate>
        <h2 style={styles.title}>Öğretmen Giriş</h2>

        <label style={styles.label}>
          Kullanıcı Adı
          <input
            style={styles.input}
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            placeholder="Kullanıcı adınızı girin"
            autoComplete="username"
            disabled={loading}
          />
        </label>

        <label style={styles.label}>
          Şifre
          <input
            style={styles.input}
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Şifrenizi girin"
            autoComplete="current-password"
            disabled={loading}
          />
        </label>

        <button style={loading ? styles.buttonDisabled : styles.button} type="submit" disabled={loading}>
          {loading ? 'Giriş Yapılıyor...' : 'Giriş Yap'}
        </button>

        {message && (
          <p style={{ ...styles.message, color: message.startsWith('✅') ? 'green' : 'red' }}>
            {message}
          </p>
        )}
      </form>
    </div>
  );
}

const styles = {
  wrapper: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    padding: '20px',
  },
  form: {
    backgroundColor: 'white',
    borderRadius: 16,
    padding: '40px 30px',
    boxShadow: '0 10px 30px rgba(0,0,0,0.15)',
    width: '100%',
    maxWidth: 400,
    boxSizing: 'border-box',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  title: {
    textAlign: 'center',
    marginBottom: 30,
    fontSize: '1.8rem',
    color: '#333',
    fontWeight: '700',
  },
  label: {
    display: 'block',
    fontWeight: '600',
    marginBottom: 10,
    color: '#555',
    fontSize: '1rem',
  },
  input: {
    width: '100%',
    padding: '12px 14px',
    fontSize: '1rem',
    borderRadius: 8,
    border: '1.5px solid #ccc',
    marginTop: 6,
    boxSizing: 'border-box',
    transition: 'border-color 0.3s ease',
  },
  button: {
    marginTop: 24,
    width: '100%',
    padding: '14px',
    fontSize: '1.1rem',
    backgroundColor: '#5a67d8',
    color: 'white',
    fontWeight: '700',
    border: 'none',
    borderRadius: 10,
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    marginTop: 24,
    width: '100%',
    padding: '14px',
    fontSize: '1.1rem',
    backgroundColor: '#a3aed0',
    color: '#fff',
    fontWeight: '700',
    border: 'none',
    borderRadius: 10,
    cursor: 'not-allowed',
  },
  message: {
    marginTop: 18,
    textAlign: 'center',
    fontWeight: '600',
    fontSize: '1rem',
  },
};
