import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function StudentDashboard({ name, id }) {
  const [message, setMessage] = useState('');
  const [schedule, setSchedule] = useState(null);
  const [examScores, setExamScores] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [selectedTeacher, setSelectedTeacher] = useState('');
  const [teacherMessage, setTeacherMessage] = useState('');
  const [scheduleSelected, setScheduleSelected] = useState(false);

  useEffect(() => {
    if (!id) return;
    fetchSchedule();
    fetchExamScores();
    fetchTeachers();
  }, [id]);

  const fetchSchedule = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/student/${id}/schedule`);
      if (res.data) {
        setSchedule(res.data);
        setScheduleSelected(true);
        setMessage('');
      } else {
        setSchedule(null);
        setScheduleSelected(false);
      }
    } catch (err) {
      if (err.response && typeof err.response.data === 'object') {
        setMessage(JSON.stringify(err.response.data));
      } else {
        setMessage('Ders programı alınamadı.');
      }
    }
  };

  const fetchExamScores = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/student/${id}/exams`);
      setExamScores(res.data || []);
      setMessage('');
    } catch (err) {
      if (err.response && typeof err.response.data === 'object') {
        setMessage(JSON.stringify(err.response.data));
      } else {
        setMessage('Sınav notları alınamadı.');
      }
    }
  };

  const fetchTeachers = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/teachers');
      setTeachers(res.data || []);
      setMessage('');
    } catch (err) {
      if (err.response && typeof err.response.data === 'object') {
        setMessage(JSON.stringify(err.response.data));
      } else {
        setMessage('Öğretmenler alınamadı.');
      }
    }
  };

  const handleSendMessage = async () => {
    if (!selectedTeacher || !teacherMessage.trim()) {
      alert('Lütfen öğretmen seçin ve mesajınızı yazın.');
      return;
    }
    try {
      await axios.post('http://localhost:8080/api/messages', {
        studentId: id,
        teacherId: selectedTeacher,
        message: teacherMessage.trim(),
      });
      alert('Mesaj gönderildi!');
      setTeacherMessage('');
      setSelectedTeacher('');
    } catch (err) {
      if (err.response && typeof err.response.data === 'object') {
        alert(JSON.stringify(err.response.data));
      } else {
        alert('Mesaj gönderilemedi.');
      }
    }
  };

  const handleScheduleSelect = async (e) => {
    const selectedSchedule = e.target.value;
    try {
      await axios.post(`http://localhost:8080/api/student/${id}/schedule`, {
        schedule: selectedSchedule,
      });
      setSchedule({ name: selectedSchedule });
      setScheduleSelected(true);
      setMessage('');
    } catch (err) {
      if (err.response && typeof err.response.data === 'object') {
        alert(JSON.stringify(err.response.data));
      } else {
        alert('Ders programı seçilemedi.');
      }
    }
  };

  return (
    <div style={styles.container}>
      <h1>Hoşgeldiniz, {name}!</h1>

      {message && (
        <p style={{ color: 'red' }}>
          {typeof message === 'string' ? message : JSON.stringify(message)}
        </p>
      )}

      {/* Ders Programı */}
      <section style={styles.section}>
        <h2>Ders Programınız</h2>
        {!scheduleSelected ? (
          <div>
            <p>Ders programınızı seçiniz:</p>
            <select onChange={handleScheduleSelect} defaultValue="">
              <option value="" disabled>
                Program Seçin
              </option>
              <option value="Program A">Program A</option>
              <option value="Program B">Program B</option>
              <option value="Program C">Program C</option>
            </select>
          </div>
        ) : (
          <p>
            <strong>Seçilen Program:</strong> {schedule.name}
          </p>
        )}
      </section>

      {/* Sınav Notları */}
      <section style={styles.section}>
        <h2>Sınav Notlarınız</h2>
        {examScores.length === 0 ? (
          <p>Sınav notunuz bulunmamaktadır.</p>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th>Ders</th>
                <th>Not</th>
              </tr>
            </thead>
            <tbody>
              {examScores.map((score, idx) => (
                <tr key={idx}>
                  <td>{score.course}</td>
                  <td>{score.grade}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Öğretmene Mesaj */}
      <section style={styles.section}>
        <h2>Öğretmene Mesaj Gönder</h2>
        <select
          value={selectedTeacher}
          onChange={(e) => setSelectedTeacher(e.target.value)}
          style={styles.select}
        >
          <option value="">Öğretmen Seçin</option>
          {teachers.map((t) => (
            <option key={t.id} value={t.id}>
              {t.name}
            </option>
          ))}
        </select>
        <textarea
          placeholder="Mesajınızı yazınız..."
          value={teacherMessage}
          onChange={(e) => setTeacherMessage(e.target.value)}
          style={styles.textarea}
        />
        <button onClick={handleSendMessage} style={styles.button}>
          Gönder
        </button>
      </section>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
    backgroundColor: '#fefefe',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  section: {
    marginBottom: '30px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  select: {
    width: '100%',
    padding: '8px',
    marginBottom: '10px',
  },
  textarea: {
    width: '100%',
    minHeight: '80px',
    padding: '8px',
    marginBottom: '10px',
    resize: 'vertical',
  },
  button: {
    padding: '10px 15px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};
