import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/auth/teacher/me`, {
          params: { username }
        });
        setTeacher(response.data);
      } catch (error) {
        console.error('Öğretmen verisi alınırken hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTeacher();
  }, [username]);

  const handleAddStudent = () => navigate('/ogrenci-ekle');
  const handleShowStudents = () => navigate('/ogrenci-listesi');
  const handleViewMessages = () => navigate('/ogretmen-mesajlar');
  const handleSendMessage = () => navigate('/teacher/send-message', { state: { teacher } });
  const handleSendAnnouncement = () => navigate('/teacher/send-announcement', { state: { teacher } });
  const handleLogout = () => navigate('/teacher-login');

  if (loading) return <div>Yükleniyor...</div>;
  if (!teacher) return <div>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Hoşgeldiniz, {teacher.name}</h2>
        <div>
          <button onClick={handleShowStudents} style={buttonStyle('#2196F3')}>Kayıtlı Öğrenciler</button>
          <button onClick={handleAddStudent} style={buttonStyle('#4CAF50')}>Öğrenci Ekle</button>
          <button onClick={handleSendMessage} style={buttonStyle('#FF9800')}>Mesaj Gönder</button>
          <button onClick={handleSendAnnouncement} style={buttonStyle('#9C27B0')}>Duyuru Yap</button>
          <button onClick={handleViewMessages} style={buttonStyle('#FF5722')}>Mesajları Görüntüle</button>
          <button onClick={handleLogout} style={buttonStyle('#f44336')}>Çıkış</button>
        </div>
      </div>
    </div>
  );
};

const buttonStyle = (bgColor) => ({
  padding: '8px 16px',
  marginRight: 10,
  backgroundColor: bgColor,
  color: 'white',
  border: 'none',
  borderRadius: 5
});

export default TeacherHomePage;
