import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './TeacherHomePage.module.css';

const messages = [
  "Bugün yeni öğrenci kayıtlarını kontrol etmeyi unutmayın.",
  "Mesajlarınızda yanıt bekleyen öğrenci olup olmadığını gözden geçirin.",
  "Öğrencilerin başarılarını artırmak için duyuru yapmayı ihmal etmeyin.",
  "Her zaman yanınızdayız, iyi çalışmalar!",
];

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);
  const [offsetX, setOffsetX] = useState(0);
  const marqueeRef = useRef(null);
  const containerRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(res.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [username]);

  // 10 saniyede mesaj değiştir
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentMessageIndex((prev) => (prev + 1) % messages.length);
      setOffsetX(0); // Mesaj değişince offset sıfırla
    }, 10000);
    return () => clearInterval(interval);
  }, []);

  // Kayan yazı animasyonu için offsetX değerini sürekli azalt
  useEffect(() => {
    let animationFrameId;
    const step = () => {
      if (!marqueeRef.current || !containerRef.current) {
        animationFrameId = requestAnimationFrame(step);
        return;
      }

      const marqueeWidth = marqueeRef.current.offsetWidth;
      const containerWidth = containerRef.current.offsetWidth;

      setOffsetX((prev) => {
        const newOffset = prev - 1; // Her frame 1px sola kaydır

        // Eğer tüm metin tamamen kaydıysa başa dön
        if (Math.abs(newOffset) > marqueeWidth) {
          return containerWidth;
        }
        return newOffset;
      });

      animationFrameId = requestAnimationFrame(step);
    };

    animationFrameId = requestAnimationFrame(step);

    return () => cancelAnimationFrame(animationFrameId);
  }, [currentMessageIndex]); // Mesaj değişince animasyon devam eder

  const handleAddStudent = () => navigate('/ogrenci-ekle');
  const handleShowStudents = () => navigate('/ogrenci-listesi');
  const handleViewMessages = () => navigate('/ogretmen-mesajlar');
  const handleSendMessage = () => navigate('/teacher/send-message', { state: { teacher } });
  const handleSendAnnouncement = () => navigate('/teacher/send-announcement', { state: { teacher } });
  const handleLogout = () => navigate('/teacher-login');

  if (loading) return <div className={styles.loading}>Yükleniyor...</div>;
  if (!teacher) return <div className={styles.error}>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h2 className={styles.title}>Hoşgeldiniz, {teacher.name}!</h2>
      </header>

      <div className={styles.marqueeWrapper} ref={containerRef}>
        <div
          className={styles.marquee}
          ref={marqueeRef}
          style={{ transform: `translateX(${offsetX}px)` }}
        >
          {messages[currentMessageIndex]}
        </div>
      </div>

      <div className={styles.buttonGroup}>
        <button className={`${styles.buttonBase} ${styles.buttonBlue}`} onClick={handleShowStudents}>
          Kayıtlı Öğrenciler
        </button>
        <button className={`${styles.buttonBase} ${styles.buttonGreen}`} onClick={handleAddStudent}>
          Öğrenci Ekle
        </button>
        <button className={`${styles.buttonBase} ${styles.buttonOrange}`} onClick={handleSendMessage}>
          Mesaj Gönder
        </button>
        <button className={`${styles.buttonBase} ${styles.buttonPurple}`} onClick={handleSendAnnouncement}>
          Duyuru Yap
        </button>
        <button className={`${styles.buttonBase} ${styles.buttonRedOrange}`} onClick={handleViewMessages}>
          Mesajları Görüntüle
        </button>
        <button className={`${styles.buttonBase} ${styles.buttonRed}`} onClick={handleLogout}>
          Çıkış
        </button>
      </div>
    </div>
  );
};

export default TeacherHomePage;
