import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const teacherRes = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(teacherRes.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  const handleAddStudent = () => navigate('/ogrenci-ekle');
  const handleShowStudents = () => navigate('/ogrenci-listesi');
  const handleViewMessages = () => navigate('/ogretmen-mesajlar');
  const handleSendMessage = () => navigate('/teacher/send-message', { state: { teacher } });
  const handleSendAnnouncement = () => navigate('/teacher/send-announcement', { state: { teacher } });
  const handleLogout = () => navigate('/teacher-login');

  if (loading) return <div>Yükleniyor...</div>;
  if (!teacher) return <div>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap' }}>
        <h2>Hoşgeldiniz, {teacher.name}</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
          <button
            onClick={handleShowStudents}
            style={buttonStyle('#2196F3')}
          >
            Kayıtlı Öğrenciler
          </button>
          <button
            onClick={handleAddStudent}
            style={buttonStyle('#4CAF50')}
          >
            Öğrenci Ekle
          </button>
          <button
            onClick={handleSendMessage}
            style={buttonStyle('#FF9800')}
          >
            Mesaj Gönder
          </button>
          <button
            onClick={handleSendAnnouncement}
            style={buttonStyle('#9C27B0')}
          >
            Duyuru Yap
          </button>
          <button
            onClick={handleViewMessages}
            style={buttonStyle('#FF5722')}
          >
            Mesajları Görüntüle
          </button>
          <button
            onClick={handleLogout}
            style={buttonStyle('#f44336')}
          >
            Çıkış
          </button>
        </div>
      </div>
    </div>
  );
};

// Buton stilleri için fonksiyon
const buttonStyle = (bgColor) => ({
  padding: '8px 16px',
  backgroundColor: bgColor,
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  fontWeight: '600',
  transition: 'background-color 0.3s ease',
});

export default TeacherHomePage;
