import React, { useState } from 'react';
import axios from 'axios';

export default function TeacherLogin({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:8080/api/auth/teacher/login', { username, password });
      setMessage('Giriş başarılı! Hoşgeldiniz ' + res.data.username);
      setUsername('');
      setPassword('');
      if (onLoginSuccess) {
        onLoginSuccess(res.data.username);  // Login başarılıysa parent'a bildir
      }
    } catch (err) {
      setMessage(err.response?.data || 'Giriş başarısız.');
    }
  };

  return (
    <div>
      <h2>Öğretmen Giriş</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Kullanıcı Adı: </label>
          <input value={username} onChange={e => setUsername(e.target.value)} required />
        </div>
        <div>
          <label>Şifre: </label>
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
        </div>
        <button type="submit">Giriş Yap</button>
      </form>
      <p>{message}</p>
    </div>
  );
}
