import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUserAlt, FaLock, FaGoogle, FaFacebookF } from 'react-icons/fa';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import Lottie from 'lottie-react';
import loadingAnimation from './loading.json'; // Bu Lottie animasyon dosyasını projenize koymalısınız

export default function TeacherLogin({ onLoginSuccess }) {
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    if (message) {
      setShowMessage(true);
      const timer = setTimeout(() => setShowMessage(false), 4000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  const formik = useFormik({
    initialValues: {
      username: '',
      password: '',
    },
    validationSchema: Yup.object({
      username: Yup.string().required('Kullanıcı adı zorunlu'),
      password: Yup.string().required('Şifre zorunlu'),
    }),
    onSubmit: async (values) => {
      setLoading(true);
      setMessage('');
      try {
        const res = await axios.post('http://localhost:8080/api/auth/teacher/login', values);
        setMessage('✅ Giriş başarılı! Hoşgeldiniz ' + res.data.username);
        formik.resetForm();
        if (onLoginSuccess) onLoginSuccess(res.data.username);
      } catch (err) {
        const errorData = err.response?.data;
        if (errorData && typeof errorData === 'object') {
          setMessage(errorData.message || JSON.stringify(errorData));
        } else {
          setMessage(errorData || '❌ Giriş başarısız.');
        }
      } finally {
        setLoading(false);
      }
    },
  });

  return (
    <div style={styles.wrapper}>
      <form style={styles.form} onSubmit={formik.handleSubmit} noValidate>
        <h2 style={styles.title}>Öğretmen Giriş</h2>

        <div style={styles.inputGroup}>
          <FaUserAlt style={styles.icon} />
          <input
            id="username"
            name="username"
            type="text"
            placeholder="Kullanıcı adınızı girin"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.username}
            disabled={loading}
            style={{
              ...styles.input,
              borderColor: formik.touched.username && formik.errors.username ? '#e74c3c' : '#ccc',
            }}
            autoComplete="username"
          />
        </div>
        {formik.touched.username && formik.errors.username ? (
          <div style={styles.error}>{formik.errors.username}</div>
        ) : null}

        <div style={styles.inputGroup}>
          <FaLock style={styles.icon} />
          <input
            id="password"
            name="password"
            type="password"
            placeholder="Şifrenizi girin"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.password}
            disabled={loading}
            style={{
              ...styles.input,
              borderColor: formik.touched.password && formik.errors.password ? '#e74c3c' : '#ccc',
            }}
            autoComplete="current-password"
          />
        </div>
        {formik.touched.password && formik.errors.password ? (
          <div style={styles.error}>{formik.errors.password}</div>
        ) : null}

        <button
          type="submit"
          style={loading ? styles.buttonDisabled : styles.button}
          disabled={loading}
        >
          {loading ? (
            <>
              <Lottie
                animationData={loadingAnimation}
                style={{ width: 24, height: 24, marginRight: 10 }}
                loop
                autoplay
              />
              Giriş Yapılıyor...
            </>
          ) : (
            'Giriş Yap'
          )}
        </button>

        <div
          style={{
            ...styles.message,
            opacity: showMessage ? 1 : 0,
            transform: showMessage ? 'translateY(0)' : 'translateY(-10px)',
            color: message.startsWith('✅') ? '#2ecc71' : '#e74c3c',
            pointerEvents: showMessage ? 'auto' : 'none',
          }}
          aria-live="polite"
          role="alert"
        >
          {message}
        </div>

        <div style={styles.socialContainer}>
          <p style={{ marginBottom: 10, color: '#555' }}>Ya da sosyal medya ile giriş yap:</p>
          <button
            type="button"
            style={{ ...styles.socialButton, backgroundColor: '#db4437' }}
            onClick={() => alert('Google ile giriş yakında!')}
            disabled={loading}
          >
            <FaGoogle style={{ marginRight: 8 }} />
            Google ile Giriş
          </button>
          <button
            type="button"
            style={{ ...styles.socialButton, backgroundColor: '#3b5998' }}
            onClick={() => alert('Facebook ile giriş yakında!')}
            disabled={loading}
          >
            <FaFacebookF style={{ marginRight: 8 }} />
            Facebook ile Giriş
          </button>
        </div>
      </form>
    </div>
  );
}

const styles = {
  wrapper: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    padding: 20,
  },
  form: {
    backgroundColor: '#fff',
    borderRadius: 16,
    padding: '40px 30px',
    boxShadow: '0 15px 35px rgba(0,0,0,0.2)',
    width: '100%',
    maxWidth: 420,
    boxSizing: 'border-box',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  title: {
    textAlign: 'center',
    marginBottom: 30,
    fontSize: '2rem',
    color: '#333',
    fontWeight: '700',
    letterSpacing: '1.5px',
  },
  inputGroup: {
    position: 'relative',
    marginBottom: 10,
  },
  icon: {
    position: 'absolute',
    top: '50%',
    left: 14,
    transform: 'translateY(-50%)',
    color: '#7f8c8d',
    fontSize: 20,
    pointerEvents: 'none',
  },
  input: {
    width: '100%',
    padding: '12px 12px 12px 42px',
    fontSize: '1rem',
    borderRadius: 8,
    border: '1.8px solid #ccc',
    outline: 'none',
    transition: 'border-color 0.3s ease, box-shadow 0.3s ease',
    boxSizing: 'border-box',
  },
  button: {
    marginTop: 20,
    width: '100%',
    padding: '14px',
    fontSize: '1.15rem',
    backgroundColor: '#5a67d8',
    color: 'white',
    fontWeight: '700',
    border: 'none',
    borderRadius: 12,
    cursor: 'pointer',
    transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
    boxShadow: '0 8px 15px rgba(90,103,216,0.3)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonDisabled: {
    marginTop: 20,
    width: '100%',
    padding: '14px',
    fontSize: '1.15rem',
    backgroundColor: '#a3aed0',
    color: '#fff',
    fontWeight: '700',
    border: 'none',
    borderRadius: 12,
    cursor: 'not-allowed',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  message: {
    marginTop: 22,
    textAlign: 'center',
    fontWeight: '600',
    fontSize: '1.1rem',
    transition: 'opacity 0.5s ease, transform 0.5s ease',
  },
  error: {
    marginTop: 4,
    marginBottom: 12,
    fontSize: '0.9rem',
    color: '#e74c3c',
    fontWeight: '600',
  },
  socialContainer: {
    marginTop: 30,
    textAlign: 'center',
  },
  socialButton: {
    border: 'none',
    padding: '12px 20px',
    margin: '0 10px',
    borderRadius: 30,
    fontSize: '1rem',
    fontWeight: '600',
    color: 'white',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    display: 'inline-flex',
    alignItems: 'center',
    userSelect: 'none',
  },
};
