import React, { useState } from 'react';
import axios from 'axios';

export default function StudentLogin() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // id artık string olduğu için parseInt kaldırıldı
      const res = await axios.post('http://localhost:8080/api/auth/student/login', {
        id,  // string olarak gönderiyoruz
        password
      });
      setMessage('Giriş başarılı! Hoşgeldiniz ' + res.data.name);
      setId('');
      setPassword('');
    } catch (err) {
      setMessage(err.response?.data || 'Giriş başarısız.');
    }
  };

  return (
    <div>
      <h2>Öğrenci Giriş</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Öğrenci Numarası: </label>
          <input
            type="text"   // burada number değil text yapıyoruz
            value={id}
            onChange={e => setId(e.target.value)}
            required
            pattern="[A-Za-z0-9]+" // sadece harf ve rakam kabul etsin
            title="Lütfen sadece harf ve rakamlardan oluşan bir numara giriniz."
          />
        </div>
        <div>
          <label>Şifre: </label>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Giriş Yap</button>
      </form>
      <p>{message}</p>
    </div>
  );
}
