import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ExamScoresPage({ id }) {
  const [examScores, setExamScores] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!id || isNaN(id)) {
      setMessage('Geçerli bir öğrenci ID’si bulunamadı.');
      return;
    }

    const fetchExamScores = async () => {
      setLoading(true);
      setMessage('');
      try {
        // Burayı /api/grades/student/{id} olarak değiştirdim
        const res = await axios.get(`http://localhost:8080/api/grades/student/${id}`);
        setExamScores(res.data || []);
        if ((res.data || []).length === 0) {
          setMessage('Henüz sınav notunuz bulunmamaktadır.');
        }
      } catch (err) {
        console.error(err);
        if (err.response && err.response.status === 404) {
          setMessage('Bu ID’ye ait öğrenci bulunamadı.');
        } else {
          setMessage('Sınav notları alınamadı.');
        }
        setExamScores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchExamScores();
  }, [id]);

  return (
    <div style={styles.container}>
      <h1>Sınav Notlarınız</h1>

      {loading && <p>Yükleniyor...</p>}

      {!loading && message && <p style={{ color: 'red' }}>{message}</p>}

      {!loading && examScores.length > 0 && (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Ders</th>
              <th>Not</th>
              <th>Sınav Türü</th>
            </tr>
          </thead>
          <tbody>
            {examScores.map((score) => (
              <tr key={score.id}>
                <td>{score.courseName || 'Bilinmiyor'}</td>
                <td>{score.gradeValue != null ? score.gradeValue : '-'}</td>
                <td>{score.examType || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    padding: '20px',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    border: '1px solid #ddd',
  },
};
