import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Öğretmen bilgisi ve öğrencileri çek
  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(res.data);

        // Öğrencileri öğretmene göre filtreleyip alabiliriz.
        // Şimdilik tüm öğrencileri çekiyoruz, backend tarafında filtreleme öneririm
        const studentsRes = await axios.get('http://localhost:8080/api/students'); // backend endpoint eklemen gerekebilir
        setStudents(studentsRes.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeacher();
  }, [username]);

  if (loading) return <div>Yükleniyor...</div>;
  if (!teacher) return <div>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Hoşgeldin, {teacher.name}!</h1>

      <section>
        <h2>Dersler</h2>
        {/* Ders bilgisi yoksa sabit veya backend’den al */}
        <ul>
          <li>Matematik</li>
          <li>Fizik</li>
          <li>Biyoloji</li>
        </ul>
      </section>

      <section>
        <h2>Öğrenci Listesi</h2>
        {students.length === 0 ? (
          <p>Kayıtlı öğrenci yok.</p>
        ) : (
          <table border="1" cellPadding="5">
            <thead>
              <tr>
                <th>Öğrenci ID</th>
                <th>İsim</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.id}</td>
                  <td>{student.name}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section>
        <h2>Not Girişi</h2>
        {/* Basit not giriş formu */}
        <NoteEntryForm students={students} />
      </section>
    </div>
  );
};

// Basit not giriş formu örneği
const NoteEntryForm = ({ students }) => {
  const [selectedStudent, setSelectedStudent] = useState('');
  const [note, setNote] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Öğrenci ID: ${selectedStudent}, Not: ${note} kaydedilecek (backend eklenmeli)`);
    // Burada backend'e not gönderme işlemi yapılacak
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Öğrenci:
        <select value={selectedStudent} onChange={(e) => setSelectedStudent(e.target.value)} required>
          <option value="">Seçiniz</option>
          {students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.name}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
        Not:
        <input
          type="number"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          min="0"
          max="100"
          required
        />
      </label>
      <br />
      <button type="submit">Notu Kaydet</button>
    </form>
  );
};

export default TeacherHomePage;
