import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OgrenciListesi = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get('http://localhost:8080/api/students');
        setStudents(res.data);
      } catch (error) {
        console.error('Öğrenci listesi alınamadı:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, []);

  if (loading) return <div>Yükleniyor...</div>;

  return (
    <div style={{ padding: 20 }}>
      <h2>Kayıtlı Öğrenciler</h2>
      {students.length === 0 ? (
        <p>Kayıtlı öğrenci yok.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', marginTop: 10 }}>
          <thead>
            <tr>
              <th>Öğrenci No</th>
              <th>Ad</th>
              <th>Soyad</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {students.map(student => (
              <tr key={student.id}>
                <td>{student.studentNumber || student.id}</td>
                <td>{student.name}</td>
                <td>{student.surname}</td>
                <td>{student.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default OgrenciListesi;
