import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TeacherHomePage = ({ username, onLogout }) => {
  const [teacher, setTeacher] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Yeni: öğrenci kayıt formundaki inputlar için state
  const [newStudentName, setNewStudentName] = useState('');
  const [newStudentUsername, setNewStudentUsername] = useState('');
  const [newStudentPassword, setNewStudentPassword] = useState('');
  const [registerError, setRegisterError] = useState('');
  const [registerSuccess, setRegisterSuccess] = useState('');

  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(res.data);

        const studentsRes = await axios.get('http://localhost:8080/api/students');
        setStudents(studentsRes.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeacher();
  }, [username]);

  // Yeni: öğrenci kayıt fonksiyonu
  const handleRegisterStudent = async (e) => {
    e.preventDefault();
    setRegisterError('');
    setRegisterSuccess('');

    if (!newStudentName || !newStudentUsername || !newStudentPassword) {
      setRegisterError('Lütfen tüm alanları doldurun.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/students', {
        name: newStudentName,
        username: newStudentUsername,
        password: newStudentPassword,
      });

      // Başarılı kayıt sonrası:
      setRegisterSuccess('Öğrenci başarıyla kaydedildi!');
      setNewStudentName('');
      setNewStudentUsername('');
      setNewStudentPassword('');

      // Öğrencileri tekrar çek
      const studentsRes = await axios.get('http://localhost:8080/api/students');
      setStudents(studentsRes.data);

    } catch (err) {
      console.error(err);
      setRegisterError('Kayıt sırasında bir hata oluştu.');
    }
  };

  if (loading) return <div style={styles.loading}>Yükleniyor...</div>;
  if (!teacher) return <div style={styles.error}>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2 style={styles.welcomeText}>Hoşgeldin, <span style={{ color: '#4caf50' }}>{teacher?.name || username}</span>!</h2>
        <button 
          onClick={onLogout} 
          style={styles.logoutButton}
          onMouseOver={e => e.currentTarget.style.backgroundColor = '#d32f2f'}
          onMouseOut={e => e.currentTarget.style.backgroundColor = '#f44336'}
        >
          Çıkış
        </button>
      </header>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Dersler</h3>
        <ul style={styles.list}>
          <li>Matematik</li>
          <li>Fizik</li>
          <li>Biyoloji</li>
        </ul>
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Öğrenci Listesi</h3>
        {students.length === 0 ? (
          <p>Kayıtlı öğrenci yok.</p>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>Öğrenci ID</th>
                <th style={styles.th}>İsim</th>
              </tr>
            </thead>
            <tbody>
              {students.map(student => (
                <tr key={student.id} style={styles.tr}>
                  <td style={styles.td}>{student.id}</td>
                  <td style={styles.td}>{student.name}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Not Girişi</h3>
        <NoteEntryForm students={students} />
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Öğrenci Kayıt</h3>
        <form onSubmit={handleRegisterStudent} style={styles.form}>
          <label style={styles.label}>
            İsim:
            <input
              type="text"
              value={newStudentName}
              onChange={e => setNewStudentName(e.target.value)}
              required
              style={styles.input}
              placeholder="Öğrenci adı"
            />
          </label>

          <label style={styles.label}>
            Kullanıcı Adı:
            <input
              type="text"
              value={newStudentUsername}
              onChange={e => setNewStudentUsername(e.target.value)}
              required
              style={styles.input}
              placeholder="Kullanıcı adı"
            />
          </label>

          <label style={styles.label}>
            Şifre:
            <input
              type="password"
              value={newStudentPassword}
              onChange={e => setNewStudentPassword(e.target.value)}
              required
              style={styles.input}
              placeholder="Şifre"
            />
          </label>

          <button type="submit" style={styles.submitButton}>Öğrenciyi Kaydet</button>
        </form>

        {registerError && <p style={{ color: 'red', marginTop: 10 }}>{registerError}</p>}
        {registerSuccess && <p style={{ color: 'green', marginTop: 10 }}>{registerSuccess}</p>}
      </section>
    </div>
  );
};

const NoteEntryForm = ({ students }) => {
  const [selectedStudent, setSelectedStudent] = useState('');
  const [note, setNote] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Öğrenci ID: ${selectedStudent}, Not: ${note} kaydedilecek (backend eklenmeli)`);
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <label style={styles.label}>
        Öğrenci:
        <select 
          value={selectedStudent} 
          onChange={e => setSelectedStudent(e.target.value)} 
          required
          style={styles.select}
        >
          <option value="">Seçiniz</option>
          {students.map(student => (
            <option key={student.id} value={student.id}>{student.name}</option>
          ))}
        </select>
      </label>

      <label style={styles.label}>
        Not:
        <input
          type="number"
          value={note}
          onChange={e => setNote(e.target.value)}
          min="0"
          max="100"
          required
