import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function StudentDashboard() {
  const [schedule, setSchedule] = useState(null);
  const [message, setMessage] = useState('');
  const [id, setId] = useState(null);
  const [name, setName] = useState('');

  useEffect(() => {
    const storedId = localStorage.getItem('userId');
    const storedName = localStorage.getItem('userName');
    if (!storedId || !storedName) {
      // Eğer yoksa login sayfasına yönlendirebilirsin
      window.location.href = '/login';
      return;
    }
    setId(storedId);
    setName(storedName);
  }, []);

  useEffect(() => {
    if (!id) return;
    fetchSchedule();
  }, [id]);

  const fetchSchedule = async () => {
    try {
      const res = await fetch(`http://localhost:8080/api/student/${id}/schedule`);
      if (!res.ok) throw new Error('Ders programı bulunamadı.');
      const data = await res.json();
      setSchedule(data);
      setMessage('');
    } catch (err) {
      setSchedule(null);
      setMessage(err.message);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Hoşgeldiniz, {name}!</h1>

      <div style={styles.navButtons}>
        <Link to="/schedule" style={styles.navButton}>Ders Programı</Link>
        <Link to="/exams" style={styles.navButton}>Sınav Notları</Link>
        <Link to="/message-teacher" style={styles.navButton}>Öğretmene Mesaj</Link>
      </div>

      <section style={styles.section}>
        <h2>Seçilen Ders Programı</h2>
        {message && <p style={{ color: 'red' }}>{message}</p>}
        {schedule ? (
          <p><strong>{schedule.name}</strong></p>
        ) : (
          <p>Ders programınız seçilmemiş.</p>
        )}
      </section>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
    backgroundColor: '#fefefe',
    borderRadius: '8px',
    boxShadow: '0 0 12px rgba(0,0,0,0.12)',
    textAlign: 'center',
  },
  navButtons: {
    display: 'flex',
    justifyContent: 'center',
    gap: '15px',
    marginBottom: '30px',
  },
  navButton: {
    flex: '1',
    padding: '12px 20px',
    backgroundColor: '#007bff',
    color: 'white',
    borderRadius: '6px',
    fontSize: '16px',
    textDecoration: 'none',
    textAlign: 'center',
  },
  section: {
    marginTop: '20px',
  },
};
