import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function SchedulePage({ id }) {
  const [schedule, setSchedule] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!id) return;

    const fetchSchedule = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/student/${id}/schedule`);
        setSchedule(res.data);
        setMessage('');
      } catch (err) {
        setMessage('Ders programı alınamadı.');
      }
    };

    fetchSchedule();
  }, [id]);

  return (
    <div style={styles.container}>
      <h1>Ders Programınız</h1>
      {message && <p style={{ color: 'red' }}>{message}</p>}
      {schedule ? (
        <p><strong>{schedule.name}</strong></p>
      ) : (
        <p>Ders programı bulunmamaktadır.</p>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    padding: '20px',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
};
