import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate, useLocation } from 'react-router-dom';

import TeacherRegister from './pages/TeacherRegister';
import TeacherLogin from './pages/TeacherLogin';
import StudentLogin from './pages/StudentLogin';
import TeacherHomePage from './pages/TeacherHomePage';
import StudentDashboard from './pages/StudentDashboard'; // Bu, senin öğrenci ana sayfan

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const location = useLocation();
  const navigate = useNavigate();

  // Navbar sadece login ve register sayfalarında gösterilecek
  const showNav = ['/student-login', '/teacher-login', '/teacher-register'].includes(location.pathname);

  // Öğrenci bilgileri state
  const [studentName, setStudentName] = React.useState('');
  const [studentId, setStudentId] = React.useState('');

  // Öğrenci giriş başarılı olduğunda çağrılır
  const handleStudentLoginSuccess = (name, id) => {
    setStudentName(name);
    setStudentId(id);
    navigate('/student/home');
  };

  return (
    <div style={{ maxWidth: 700, margin: 'auto', padding: 20, fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h1>Not Takip Sistemi</h1>

      {showNav && (
        <nav style={{ marginBottom: 20 }}>
          <Link to="/student-login" style={{ marginRight: 15 }}>
            Öğrenci Giriş
          </Link>
          <Link to="/teacher-login" style={{ marginRight: 15 }}>
            Öğretmen Giriş
          </Link>
          <Link to="/teacher-register">Öğretmen Kayıt</Link>
        </nav>
      )}

      <Routes>
        <Route
          path="/student-login"
          element={<StudentLogin onLoginSuccess={handleStudentLoginSuccess} />}
        />
        <Route path="/teacher-login" element={<TeacherLoginWrapper />} />
        <Route path="/teacher-register" element={<TeacherRegister />} />
        <Route path="/teacher/home" element={<TeacherHomePageWrapper />} />

        <Route
          path="/student/home"
          element={
            studentName && studentId ? (
              <StudentDashboard name={studentName} id={studentId} />
            ) : (
              <div>Lütfen önce giriş yapınız.</div>
            )
          }
        />

        <Route path="/" element={<div>Ana sayfa. Lütfen giriş yapınız veya kayıt olunuz.</div>} />
      </Routes>
    </div>
  );
}

function TeacherLoginWrapper() {
  const navigate = useNavigate();

  const handleLoginSuccess = (username) => {
    navigate('/teacher/home', { state: { username } });
  };

  return <TeacherLogin onLoginSuccess={handleLoginSuccess} />;
}

function TeacherHomePageWrapper() {
  const location = useLocation();
  const username = location.state?.username || 'Öğretmen';

  return <TeacherHomePage username={username} />;
}

export default App;
