import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TeacherHomePage = ({ username, onLogout }) => {
  const [teacher, setTeacher] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(res.data);

        const studentsRes = await axios.get('http://localhost:8080/api/students');
        setStudents(studentsRes.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeacher();
  }, [username]);

  if (loading) return <div style={styles.loading}>Yükleniyor...</div>;
  if (!teacher) return <div style={styles.error}>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2 style={styles.welcomeText}>Hoşgeldin, <span style={{ color: '#4caf50' }}>{teacher?.name || username}</span>!</h2>
        <button 
          onClick={onLogout} 
          style={styles.logoutButton}
          onMouseOver={e => e.currentTarget.style.backgroundColor = '#d32f2f'}
          onMouseOut={e => e.currentTarget.style.backgroundColor = '#f44336'}
        >
          Çıkış
        </button>
      </header>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Dersler</h3>
        <ul style={styles.list}>
          <li>Matematik</li>
          <li>Fizik</li>
          <li>Biyoloji</li>
        </ul>
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Öğrenci Listesi</h3>
        {students.length === 0 ? (
          <p>Kayıtlı öğrenci yok.</p>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>Öğrenci ID</th>
                <th style={styles.th}>İsim</th>
              </tr>
            </thead>
            <tbody>
              {students.map(student => (
                <tr key={student.id} style={styles.tr}>
                  <td style={styles.td}>{student.id}</td>
                  <td style={styles.td}>{student.name}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Not Girişi</h3>
        <NoteEntryForm students={students} />
      </section>
    </div>
  );
};

const NoteEntryForm = ({ students }) => {
  const [selectedStudent, setSelectedStudent] = useState('');
  const [note, setNote] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Öğrenci ID: ${selectedStudent}, Not: ${note} kaydedilecek (backend eklenmeli)`);
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <label style={styles.label}>
        Öğrenci:
        <select 
          value={selectedStudent} 
          onChange={e => setSelectedStudent(e.target.value)} 
          required
          style={styles.select}
        >
          <option value="">Seçiniz</option>
          {students.map(student => (
            <option key={student.id} value={student.id}>{student.name}</option>
          ))}
        </select>
      </label>

      <label style={styles.label}>
        Not:
        <input
          type="number"
          value={note}
          onChange={e => setNote(e.target.value)}
          min="0"
          max="100"
          required
          style={styles.input}
        />
      </label>

      <button type="submit" style={styles.submitButton}>Notu Kaydet</button>
    </form>
  );
};

const styles = {
  container: {
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#333',
    maxWidth: 700,
    margin: 'auto',
    backgroundColor: '#fefefe',
    borderRadius: 8,
    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 30,
    borderBottom: '1px solid #ddd',
    paddingBottom: 10,
  },
  welcomeText: {
    fontWeight: 600,
    fontSize: '1.5rem',
  },
  logoutButton: {
    padding: '8px 16px',
    backgroundColor: '#f44336',
    color: '#fff',
    border: 'none',
    borderRadius: 6,
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease',
  },
  section: {
    marginBottom: 30,
  },
  sectionTitle: {
    borderBottom: '2px solid #4caf50',
    paddingBottom: 5,
    marginBottom: 15,
    fontWeight: 600,
  },
  list: {
    listStyleType: 'circle',
    paddingLeft: 20,
    fontSize: 16,
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
  },
  th: {
    borderBottom: '2px solid #4caf50',
    textAlign: 'left',
    padding: '10px 15px',
    backgroundColor: '#e8f5e9',
  },
  tr: {
    borderBottom: '1px solid #ddd',
  },
  td: {
    padding: '10px 15px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: 15,
    maxWidth: 400,
  },
  label: {
    display: 'flex',
    flexDirection: 'column',
    fontWeight: 500,
    fontSize: 14,
  },
  select: {
    marginTop: 6,
    padding: 8,
    fontSize: 14,
    borderRadius: 4,
    border: '1px solid #ccc',
  },
  input: {
    marginTop: 6,
    padding: 8,
    fontSize: 14,
    borderRadius: 4,
    border: '1px solid #ccc',
  },
  submitButton: {
    padding: '10px 16px',
    backgroundColor: '#4caf50',
    color: 'white',
    fontWeight: 'bold',
    border: 'none',
    borderRadius: 6,
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  loading: {
    textAlign: 'center',
    marginTop: 100,
    fontSize: 18,
    color: '#777',
  },
  error: {
    textAlign: 'center',
    marginTop: 100,
    fontSize: 18,
    color: '#d32f2f',
  }
};

export default TeacherHomePage;
