import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUserAlt, FaLock } from 'react-icons/fa';

export default function TeacherLogin({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showMessage, setShowMessage] = useState(false);

  // Mesajı gösterdikten 4 saniye sonra gizle
  useEffect(() => {
    if (message) {
      setShowMessage(true);
      const timer = setTimeout(() => setShowMessage(false), 4000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    try {
      const res = await axios.post('http://localhost:8080/api/auth/teacher/login', { username, password });
      setMessage('✅ Giriş başarılı! Hoşgeldiniz ' + res.data.username);
      setUsername('');
      setPassword('');
      if (onLoginSuccess) onLoginSuccess(res.data.username);
    } catch (err) {
      const errorData = err.response?.data;
      if (errorData && typeof errorData === 'object') {
        setMessage(errorData.message || JSON.stringify(errorData));
      } else {
        setMessage(errorData || '❌ Giriş başarısız.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.wrapper}>
      <form style={styles.form} onSubmit={handleLogin} noValidate>
        <h2 style={styles.title}>Öğretmen Giriş</h2>

        <div style={styles.inputGroup}>
          <FaUserAlt style={styles.icon} />
          <input
            style={styles.input}
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            placeholder="Kullanıcı adınızı girin"
            autoComplete="username"
            disabled={loading}
          />
        </div>

        <div style={styles.inputGroup}>
          <FaLock style={styles.icon} />
          <input
            style={styles.input}
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Şifrenizi girin"
            autoComplete="current-password"
            disabled={loading}
          />
        </div>

        <button style={loading ? styles.buttonDisabled : styles.button} type="submit" disabled={loading}>
          {loading ? 'Giriş Yapılıyor...' : 'Giriş Yap'}
        </button>

        <div
          style={{
            ...styles.message,
            opacity: showMessage ? 1 : 0,
            transform: showMessage ? 'translateY(0)' : 'translateY(-10px)',
            color: message.startsWith('✅') ? '#2ecc71' : '#e74c3c',
            pointerEvents: showMessage ? 'auto' : 'none',
          }}
          aria-live="polite"
          role="alert"
        >
          {message}
        </div>
      </form>
    </div>
  );
}

const styles = {
  wrapper: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    padding: 20,
  },
  form: {
    backgroundColor: '#fff',
    borderRadius: 16,
    padding: '40px 30px',
    boxShadow: '0 15px 35px rgba(0,0,0,0.2)',
    width: '100%',
    maxWidth: 420,
    boxSizing: 'border-box',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  title: {
    textAlign: 'center',
    marginBottom: 30,
    fontSize: '2rem',
    color: '#333',
    fontWeight: '700',
    letterSpacing: '1.5px',
  },
  inputGroup: {
    position: 'relative',
    marginBottom: 25,
  },
  icon: {
    position: 'absolute',
    top: '50%',
    left: 14,
    transform: 'translateY(-50%)',
    color: '#7f8c8d',
    fontSize: 20,
    pointerEvents: 'none',
  },
  input: {
    width: '100%',
    padding: '12px 12px 12px 42px',
    fontSize: '1rem',
    borderRadius: 8,
    border: '1.8px solid #ccc',
    outline: 'none',
    transition: 'border-color 0.3s ease, box-shadow 0.3s ease',
    boxSizing: 'border-box',
  },
  button: {
    marginTop: 12,
    width: '100%',
    padding: '14px',
    fontSize: '1.15rem',
    backgroundColor: '#5a67d8',
    color: 'white',
    fontWeight: '700',
    border: 'none',
    borderRadius: 12,
    cursor: 'pointer',
    transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
    boxShadow: '0 8px 15px rgba(90,103,216,0.3)',
  },
  buttonDisabled: {
    marginTop: 12,
    width: '100%',
    padding: '14px',
    fontSize: '1.15rem',
    backgroundColor: '#a3aed0',
    color: '#fff',
    fontWeight: '700',
    border: 'none',
    borderRadius: 12,
    cursor: 'not-allowed',
  },
  message: {
    marginTop: 22,
    textAlign: 'center',
    fontWeight: '600',
    fontSize: '1.1rem',
    transition: 'opacity 0.5s ease, transform 0.5s ease',
  },
};
