import React, { useState } from 'react';
import axios from 'axios';

export default function StudentLogin() {
  const [name, setName] = useState('');
  const [id, setId] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:8080/api/auth/student/login', { id: parseInt(id), name });
      setMessage('Giriş başarılı! Hoşgeldiniz ' + res.data.name);
      setName('');
      setId('');
    } catch (err) {
      setMessage(err.response?.data || 'Giriş başarısız.');
    }
  };

  return (
    <div>
      <h2>Öğrenci Giriş</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Öğrenci ID: </label>
          <input value={id} onChange={e => setId(e.target.value)} required />
        </div>
        <div>
          <label>Adınız: </label>
          <input value={name} onChange={e => setName(e.target.value)} required />
        </div>
        <button type="submit">Giriş Yap</button>
      </form>
      <p>{message}</p>
    </div>
  );
}
