import React, { useState } from 'react';
import axios from 'axios';

const AnnouncementForm = ({ teacherUsername }) => {
  const [announcementText, setAnnouncementText] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!announcementText.trim()) {
      setStatus('Duyuru boş olamaz.');
      return;
    }

    try {
      // Tüm öğrencilere mesaj atmak için backend'de bir endpoint olmalı (örneğin /api/messages/announcement)
      await axios.post('http://localhost:8080/api/messages/announcement', {
        teacher: { username: teacherUsername },
        content: announcementText,
      });
      setStatus('Duyuru gönderildi.');
      setAnnouncementText('');
    } catch (err) {
      console.error(err);
      setStatus('Duyuru gönderilirken hata oluştu.');
    }
  };

  return (
    <div style={{ marginTop: 40 }}>
      <h3>Tüm Öğrencilere Duyuru Gönder</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Duyuru metnini yazınız"
          value={announcementText}
          onChange={(e) => setAnnouncementText(e.target.value)}
          style={{ padding: 8, width: '400px', marginRight: 10 }}
        />
        <button type="submit" style={{ padding: '8px 16px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: 4 }}>
          Gönder
        </button>
      </form>
      {status && <p>{status}</p>}
    </div>
  );
};

export default AnnouncementForm;
