import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import MessageForm from './MessageForm';
import AnnouncementForm from './AnnouncementForm';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const teacherRes = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(teacherRes.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  const handleAddStudent = () => {
    navigate('/ogrenci-ekle');
  };

  const handleShowStudents = () => {
    navigate('/ogrenci-listesi');
  };

  const handleViewMessages = () => {
    navigate('/ogretmen-mesajlar');
  };

  const handleLogout = () => {
    navigate('/teacher-login');
  };

  if (loading) return <div>Yükleniyor...</div>;
  if (!teacher) return <div>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Hoşgeldiniz, {teacher.name}</h2>
        <div>
          <button
            onClick={handleShowStudents}
            style={{
              padding: '8px 16px',
              marginRight: 10,
              backgroundColor: '#2196F3',
              color: '#fff',
              border: 'none',
              borderRadius: 5,
            }}
          >
            Kayıtlı Öğrenciler
          </button>
          <button
            onClick={handleAddStudent}
            style={{
              padding: '8px 16px',
              marginRight: 10,
              backgroundColor: '#4CAF50',
              color: '#fff',
              border: 'none',
              borderRadius: 5,
            }}
          >
            Öğrenci Ekle
          </button>
          <button
            onClick={handleViewMessages}
            style={{
              padding: '8px 16px',
              marginRight: 10,
              backgroundColor: '#FF9800',
              color: 'white',
              border: 'none',
              borderRadius: 5,
            }}
          >
            Mesajları Görüntüle
          </button>
          <button
            onClick={handleLogout}
            style={{
              padding: '8px 16px',
              backgroundColor: '#f44336',
              color: 'white',
              border: 'none',
              borderRadius: 5,
            }}
          >
            Çıkış
          </button>
        </div>
      </div>

      {/* Mesaj gönderme formu */}
      <MessageForm teacherUsername={teacher.username} />

      {/* Duyuru gönderme formu */}
      <AnnouncementForm teacherUsername={teacher.username} />
    </div>
  );
};

export default TeacherHomePage;
