import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const OgrenciEkle = () => {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [tcKimlik, setTcKimlik] = useState('');
  const [email, setEmail] = useState('');
  const [birthplace, setBirthplace] = useState('');
  const [mesaj, setMesaj] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMesaj('');

    if (!name || !surname || !tcKimlik || !email || !birthplace) {
      setMesaj('Lütfen tüm alanları doldurun.');
      return;
    }

    if (!/^\d{11}$/.test(tcKimlik)) {
      setMesaj('Geçerli bir 11 haneli T.C. Kimlik numarası giriniz.');
      return;
    }

    try {
      const res = await axios.post('http://localhost:8080/api/students', {
        name,
        surname,
        tcKimlik,
        email,
        birthplace,
      });

      setMesaj(`Başarıyla eklendi! Öğrenci No: ${res.data.studentNumber || '-'} / Şifre: (gizli)`);
      setName('');
      setSurname('');
      setTcKimlik('');
      setEmail('');
      setBirthplace('');
    } catch (err) {
      console.error('Hata:', err);
      setMesaj('Kayıt sırasında hata oluştu.');
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '500px', margin: 'auto' }}>
      <h2>Öğrenci Ekle</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <input type="text" placeholder="Ad" value={name} onChange={e => setName(e.target.value)} required />
        <input type="text" placeholder="Soyad" value={surname} onChange={e => setSurname(e.target.value)} required />
        <input
          type="text"
          placeholder="T.C. Kimlik No"
          value={tcKimlik}
          onChange={e => setTcKimlik(e.target.value)}
          maxLength={11}
          required
        />
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
        <input type="text" placeholder="Doğum Yeri" value={birthplace} onChange={e => setBirthplace(e.target.value)} required />
        <button
          type="submit"
          style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: 5 }}
        >
          Kaydet
        </button>
        {mesaj && <p>{mesaj}</p>}
      </form>
      <button onClick={() => navigate('/ogretmen')} style={{ marginTop: '10px' }}>
        Geri Dön
      </button>
    </div>
  );
};

export default OgrenciEkle;
