// src/pages/NotGirPage.js
import React, { useState } from 'react';

const NotGirPage = () => {
  const [ogrenciId, setOgrenciId] = useState('');
  const [dersAdi, setDersAdi] = useState('');
  const [notDegeri, setNotDegeri] = useState('');
  const [mesaj, setMesaj] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Burada API çağrısı yapabilirsiniz
    setMesaj(`Öğrenci ID: ${ogrenciId}, Ders: ${dersAdi}, Not: ${notDegeri} başarıyla kaydedildi.`);
    setOgrenciId('');
    setDersAdi('');
    setNotDegeri('');
  };

  return (
    <div>
      <h2>Öğrenci Not Girişi</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Öğrenci ID:</label><br />
          <input value={ogrenciId} onChange={e => setOgrenciId(e.target.value)} required />
        </div>
        <div>
          <label>Ders Adı:</label><br />
          <input value={dersAdi} onChange={e => setDersAdi(e.target.value)} required />
        </div>
        <div>
          <label>Not:</label><br />
          <input type="number" value={notDegeri} onChange={e => setNotDegeri(e.target.value)} required min="0" max="100" />
        </div>
        <button type="submit">Notu Kaydet</button>
      </form>
      {mesaj && <p>{mesaj}</p>}
    </div>
  );
};

export default NotGirPage;
