import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaIdBadge, FaLock } from 'react-icons/fa';
import { useFormik } from 'formik';
import * as Yup from 'yup';

export default function StudentLogin({ onLoginSuccess }) {
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    if (message) {
      setShowMessage(true);
      const timer = setTimeout(() => setShowMessage(false), 4000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  const formik = useFormik({
    initialValues: {
      studentNumber: '',
      password: '',
    },
    validationSchema: Yup.object({
      studentNumber: Yup.string()
        .matches(/^[A-Za-z0-9]+$/, 'Lütfen sadece harf ve rakamlardan oluşan bir numara giriniz.')
        .required('Öğrenci numarası zorunlu'),
      password: Yup.string().required('Şifre zorunlu'),
    }),
    onSubmit: async (values) => {
      setLoading(true);
      setMessage('');
      try {
        const res = await axios.post('http://localhost:8080/api/auth/student/login', {
          studentNumber: values.studentNumber.trim(),
          password: values.password.trim(),
        });
        setMessage('✅ Giriş başarılı! Hoşgeldiniz ' + res.data.name);
        formik.resetForm();
        if (onLoginSuccess) onLoginSuccess(res.data.name, res.data.id);
      } catch (err) {
        const errorData = err.response?.data;
        if (errorData && typeof errorData === 'object') {
          setMessage(errorData.message || JSON.stringify(errorData));
        } else {
          setMessage(errorData || '❌ Giriş başarısız.');
        }
      } finally {
        setLoading(false);
      }
    },
  });

  return (
    <div style={styles.pageWrapper}>
      <div style={styles.formWrapper}>
        <h1 style={styles.formTitle}>Öğrenci Giriş</h1>

        <form onSubmit={formik.handleSubmit} noValidate style={styles.form}>
          <label style={styles.label} htmlFor="studentNumber">
            Öğrenci Numarası
          </label>
          <div style={styles.inputGroup}>
            <FaIdBadge style={styles.icon} />
            <input
              id="studentNumber"
              name="studentNumber"
              type="text"
              placeholder="Öğrenci Numaranızı Girin"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              value={formik.values.studentNumber}
              disabled={loading}
              style={{
                ...styles.input,
                borderColor:
                  formik.touched.studentNumber && formik.errors.studentNumber ? '#e74c3c' : '#ccc',
              }}
              autoComplete="username"
            />
          </div>
          {formik.touched.studentNumber && formik.errors.studentNumber && (
            <div style={styles.error}>{formik.errors.studentNumber}</div>
          )}

          <label style={styles.label} htmlFor="password">
            Şifre
          </label>
          <div style={styles.inputGroup}>
            <FaLock style={styles.icon} />
            <input
              id="password"
              name="password"
              type="password"
              placeholder="Şifrenizi Girin"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              value={formik.values.password}
              disabled={loading}
              style={{
                ...styles.input,
                borderColor: formik.touched.password && formik.errors.password ? '#e74c3c' : '#ccc',
              }}
              autoComplete="current-password"
            />
          </div>
          {formik.touched.password && formik.errors.password && (
            <div style={styles.error}>{formik.errors.password}</div>
          )}

          <button
            type="submit"
            disabled={loading}
            style={loading ? styles.buttonDisabled : styles.button}
          >
            {loading ? 'Giriş Yapılıyor...' : 'Giriş Yap'}
          </button>

          <div
            style={{
              ...styles.message,
              opacity: showMessage ? 1 : 0,
              transform: showMessage ? 'translateY(0)' : 'translateY(-10px)',
              color: message.startsWith('✅') ? '#27ae60' : '#c0392b',
              pointerEvents: showMessage ? 'auto' : 'none',
            }}
            role="alert"
            aria-live="polite"
          >
            {message}
          </div>
        </form>
      </div>
    </div>
  );
}

const styles = {
  pageWrapper: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f6f9fc',
    padding: 20,
  },
  formWrapper: {
    backgroundColor: 'white',
    padding: 40,
    borderRadius: 16,
    boxShadow: '0 6px 20px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: 420,
    boxSizing: 'border-box',
  },
  formTitle: {
    marginBottom: 30,
    fontSize: '2.4rem',
    fontWeight: '700',
    textAlign: 'center',
    color: '#34495e',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  label: {
    marginBottom: 8,
    fontWeight: '600',
    color: '#34495e',
  },
  inputGroup: {
    position: 'relative',
    marginBottom: 20,
  },
  icon: {
    position: 'absolute',
    top: '50%',
    left: 12,
    transform: 'translateY(-50%)',
    color: '#95a5a6',
    fontSize: 18,
    pointerEvents: 'none',
  },
  input: {
    width: '100%',
    padding: '12px 12px 12px 40px',
    fontSize: '1rem',
    borderRadius: 8,
    border: '1.5px solid #ccc',
    outline: 'none',
    transition: 'border-color 0.3s ease',
    boxSizing: 'border-box',
  },
  button: {
    padding: 14,
    fontSize: '1.15rem',
    fontWeight: '700',
    color: 'white',
    backgroundColor: '#2980b9',
    border: 'none',
    borderRadius: 12,
    cursor: 'pointer',
    boxShadow: '0 8px 15px rgba(41, 128, 185, 0.3)',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    padding: 14,
    fontSize: '1.15rem',
    fontWeight: '700',
    color: '#bdc3c7',
    backgroundColor: '#ecf0f1',
    border: 'none',
    borderRadius: 12,
    cursor: 'not-allowed',
  },
  message: {
    marginTop: 24,
    textAlign: 'center',
    fontWeight: '600',
    fontSize: '1.1rem',
    transition: 'opacity 0.5s ease, transform 0.5s ease',
  },
  error: {
    marginTop: 6,
    color: '#e74c3c',
    fontWeight: '600',
    fontSize: '0.9rem',
  },
};
