import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const teacherRes = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(teacherRes.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  const handleAddStudent = () => {
    navigate('/ogrenci-ekle');
  };

  const handleShowStudents = () => {
    navigate('/ogrenci-listesi');
  };

  const handleViewMessages = () => {
    navigate('/ogretmen-mesajlar');
  };

  const handleSendMessage = () => {
    navigate('/teacher/send-message', { state: { teacher } });
  };

  const handleSendAnnouncement = () => {
    navigate('/teacher/send-announcement', { state: { teacher } });
  };

  const handleLogout = () => {
    navigate('/teacher-login');
  };

  if (loading) return <div>Yükleniyor...</div>;
  if (!teacher) return <div>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Hoşgeldiniz, {teacher.name}</h2>
        <div>
          <button
            onClick={handleShowStudents}
            style={{ padding: '8px 16px', marginRight: 10, backgroundColor: '#2196F3', color: '#fff', border: 'none', borderRadius: 5 }}
          >
            Kayıtlı Öğrenciler
          </button>
          <button
            onClick={handleAddStudent}
            style={{ padding: '8px 16px', marginRight: 10, backgroundColor: '#4CAF50', color: '#fff', border: 'none', borderRadius: 5 }}
          >
            Öğrenci Ekle
          </button>
          <button
            onClick={handleSendMessage}
            style={{ padding: '8px 16px', marginRight: 10, backgroundColor: '#FF9800', color: 'white', border: 'none', borderRadius: 5 }}
          >
            Mesaj Gönder
          </button>
          <button
            onClick={handleSendAnnouncement}
            style={{ padding: '8px 16px', marginRight: 10, backgroundColor: '#9C27B0', color: 'white', border: 'none', borderRadius: 5 }}
          >
            Duyuru Yap
          </button>
          <button
            onClick={handleViewMessages}
            style={{ padding: '8px 16px', marginRight: 10, backgroundColor: '#FF5722', color: 'white', border: 'none', borderRadius: 5 }}
          >
            Mesajları Görüntüle
          </button>
          <button
            onClick={handleLogout}
            style={{ padding: '8px 16px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: 5 }}
          >
            Çıkış
          </button>
        </div>
      </div>
    </div>
  );
};

export default TeacherHomePage;
