import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate, useLocation } from 'react-router-dom';
import TeacherRegister from './pages/TeacherRegister';
import TeacherLogin from './pages/TeacherLogin';
import StudentLogin from './pages/StudentLogin';
import TeacherHomePage from './pages/TeacherHomePage';

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const location = useLocation();

  // Sadece giriş ve kayıt sayfalarında nav göster
  const showNav = ['/student-login', '/teacher-login', '/teacher-register'].includes(location.pathname);

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h1>Not Takip Sistemi</h1>

      {showNav && (
        <nav style={{ marginBottom: 20 }}>
          <Link to="/student-login" style={{ marginRight: 10 }}>Öğrenci Giriş</Link>
          <Link to="/teacher-login" style={{ marginRight: 10 }}>Öğretmen Giriş</Link>
          <Link to="/teacher-register">Öğretmen Kayıt</Link>
        </nav>
      )}

      <Routes>
        <Route path="/student-login" element={<StudentLogin />} />
        <Route path="/teacher-login" element={<TeacherLoginWrapper />} />
        <Route path="/teacher-register" element={<TeacherRegister />} />
        <Route path="/teacher/home" element={<TeacherHomePageWrapper />} />
        <Route path="/" element={<div>Ana sayfa. Lütfen seçim yapınız.</div>} />
      </Routes>
    </div>
  );
}

function TeacherLoginWrapper() {
  const navigate = useNavigate();

  const handleLoginSuccess = (username) => {
    navigate('/teacher/home', { state: { username } });
  };

  return <TeacherLogin onLoginSuccess={handleLoginSuccess} />;
}

function TeacherHomePageWrapper() {
  const location = useLocation();
  const navigate = useNavigate();

  const username = location.state?.username;

  if (!username) {
    return <div>Lütfen önce giriş yapınız.</div>;
  }

  const handleLogout = () => {
    navigate('/');
  };

  return <TeacherHomePage username={username} onLogout={handleLogout} />;
}

export default App;
