import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function MessageTeacherPage({ id }) {
  const [teachers, setTeachers] = useState([]);
  const [selectedTeacher, setSelectedTeacher] = useState('');
  const [teacherMessage, setTeacherMessage] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchTeachers();
  }, []);

  const fetchTeachers = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/teachers');
      setTeachers(res.data || []);
      setMessage('');
    } catch (err) {
      setMessage('Öğretmen listesi alınamadı.');
    }
  };

  const handleSendMessage = async () => {
    if (!selectedTeacher || !teacherMessage.trim()) {
      alert('Lütfen öğretmen seçin ve mesajınızı yazın.');
      return;
    }
    try {
      await axios.post('http://localhost:8080/api/messages', {
        studentId: id,
        teacherId: selectedTeacher,
        message: teacherMessage.trim(),
      });
      alert('Mesaj gönderildi!');
      setTeacherMessage('');
      setSelectedTeacher('');
    } catch (err) {
      alert('Mesaj gönderilirken hata oluştu.');
    }
  };

  return (
    <div style={styles.container}>
      <h1>Öğretmene Mesaj Gönder</h1>
      {message && <p style={{ color: 'red' }}>{message}</p>}
      <select
        value={selectedTeacher}
        onChange={(e) => setSelectedTeacher(e.target.value)}
        style={styles.select}
      >
        <option value="">Öğretmen Seçin</option>
        {teachers.map((t) => (
          <option key={t.id} value={t.id}>
            {t.name}
          </option>
        ))}
      </select>
      <textarea
        placeholder="Mesajınızı yazınız..."
        value={teacherMessage}
        onChange={(e) => setTeacherMessage(e.target.value)}
        style={styles.textarea}
      />
      <button onClick={handleSendMessage} style={styles.button}>
        Gönder
      </button>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
    backgroundColor: '#fefefe',
    borderRadius: '8px',
    boxShadow: '0 0 12px rgba(0,0,0,0.12)',
  },
  select: {
    width: '100%',
    padding: '10px',
    marginBottom: '15px',
    fontSize: '16px',
  },
  textarea: {
    width: '100%',
    minHeight: '100px',
    padding: '10px',
    marginBottom: '15px',
    fontSize: '16px',
    resize: 'vertical',
  },
  button: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    fontSize: '18px',
    borderRadius: '6px',
    cursor: 'pointer',
  },
};
