import React, { useState, useEffect } from 'react';

export default function StudentDashboard({ name, id }) {
  const [schedule, setSchedule] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!id) return;
    fetchSchedule();
  }, [id]);

  const fetchSchedule = async () => {
    try {
      const res = await fetch(`http://localhost:8080/api/student/${id}/schedule`);
      if (!res.ok) throw new Error('Ders programı bulunamadı.');
      const data = await res.json();
      setSchedule(data);
      setMessage('');
    } catch (err) {
      setSchedule(null);
      setMessage(err.message);
    }
  };

  const goTo = (path) => {
    // React Router varsa bunu değiştir
    window.location.href = path;
  };

  return (
    <div style={styles.container}>
      <h1>Hoşgeldiniz, {name}!</h1>

      <div style={styles.navButtons}>
        <button onClick={() => goTo('/schedule')} style={styles.navButton}>
          Ders Programı
        </button>
        <button onClick={() => goTo('/exams')} style={styles.navButton}>
          Sınav Notları
        </button>
        <button onClick={() => goTo('/message-teacher')} style={styles.navButton}>
          Öğretmene Mesaj
        </button>
      </div>

      <section style={styles.section}>
        <h2>Seçilen Ders Programı</h2>
        {message && <p style={{ color: 'red' }}>{message}</p>}
        {schedule ? (
          <p>
            <strong>{schedule.name}</strong>
          </p>
        ) : (
          <p>Ders programınız seçilmemiş.</p>
        )}
      </section>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
    backgroundColor: '#fefefe',
    borderRadius: '8px',
    boxShadow: '0 0 12px rgba(0,0,0,0.12)',
    textAlign: 'center',
  },
  navButtons: {
    display: 'flex',
    justifyContent: 'center',
    gap: '15px',
    marginBottom: '30px',
  },
  navButton: {
    flex: '1',
    padding: '12px 20px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  section: {
    marginTop: '20px',
  },
};
