import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TeacherHomePage = ({ username, onLogout }) => {
  const [teacher, setTeacher] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Yeni: öğrenci kayıt formundaki inputlar için state
  const [newStudentName, setNewStudentName] = useState('');
  const [newStudentSurname, setNewStudentSurname] = useState('');
  const [newStudentTCKimlik, setNewStudentTCKimlik] = useState('');
  const [newStudentBirthplace, setNewStudentBirthplace] = useState('');
  const [newStudentEmail, setNewStudentEmail] = useState('');
  const [registerError, setRegisterError] = useState('');
  const [registerSuccess, setRegisterSuccess] = useState('');

  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(res.data);

        const studentsRes = await axios.get('http://localhost:8080/api/students');
        setStudents(studentsRes.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeacher();
  }, [username]);

  // Yeni: öğrenci kayıt fonksiyonu
  const handleRegisterStudent = async (e) => {
    e.preventDefault();
    setRegisterError('');
    setRegisterSuccess('');

    // Alan kontrolü
    if (!newStudentName || !newStudentSurname || !newStudentTCKimlik || !newStudentBirthplace || !newStudentEmail) {
      setRegisterError('Lütfen tüm alanları doldurun.');
      return;
    }

    // TC kimlik numarası kontrolü (11 hane sayısal kontrol basit)
    if (!/^\d{11}$/.test(newStudentTCKimlik)) {
      setRegisterError('Geçerli bir 11 haneli T.C. Kimlik numarası giriniz.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/students', {
        name: newStudentName,
        surname: newStudentSurname,
        tcKimlik: newStudentTCKimlik,
        birthplace: newStudentBirthplace,
        email: newStudentEmail,
      });

      // Başarılı kayıt sonrası mesaj (backend öğrenci numarası ve şifreyi dönebilir)
      setRegisterSuccess(`Öğrenci başarıyla kaydedildi! Öğrenci No: ${response.data.studentNumber || '-'}, Şifre: ${response.data.password ? '(gizli)' : '-'}`);

      // Formu sıfırla
      setNewStudentName('');
      setNewStudentSurname('');
      setNewStudentTCKimlik('');
      setNewStudentBirthplace('');
      setNewStudentEmail('');

      // Öğrencileri tekrar çek
      const studentsRes = await axios.get('http://localhost:8080/api/students');
      setStudents(studentsRes.data);

    } catch (err) {
      console.error(err);
      setRegisterError('Kayıt sırasında bir hata oluştu.');
    }
  };

  if (loading) return <div style={styles.loading}>Yükleniyor...</div>;
  if (!teacher) return <div style={styles.error}>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2 style={styles.welcomeText}>Hoşgeldin, <span style={{ color: '#4caf50' }}>{teacher?.name || username}</span>!</h2>
        <button 
          onClick={onLogout} 
          style={styles.logoutButton}
          onMouseOver={e => e.currentTarget.style.backgroundColor = '#d32f2f'}
          onMouseOut={e => e.currentTarget.style.backgroundColor = '#f44336'}
        >
          Çıkış
        </button>
      </header>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Dersler</h3>
        <ul style={styles.list}>
          <li>Matematik</li>
          <li>Fizik</li>
          <li>Biyoloji</li>
        </ul>
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Öğrenci Listesi</h3>
        {students.length === 0 ? (
          <p>Kayıtlı öğrenci yok.</p>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>Öğrenci ID</th>
                <th style={styles.th}>İsim</th>
                <th style={styles.th}>Soyisim</th>
              </tr>
            </thead>
            <tbody>
              {students.map(student => (
                <tr key={student.id} style={styles.tr}>
                  <td style={styles.td}>{student.studentNumber || student.id}</td>
                  <td style={styles.td}>{student.name}</td>
                  <td style={styles.td}>{student.surname}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section style={styles.section}>
        <h3 style={styles.sectionTitle}>Öğrenci Kayıt</h3>
        <form onSubmit={handleRegisterStudent} style={styles.form}>
          <label style={styles.label}>
            İsim:
            <input
              type="text"
              value={newStudentName}
              onChange={e => setNewStudentName(e.target.value)}
              required
              style={styles.input}
              placeholder="Öğrenci adı"
            />
          </label>

          <label style={styles.label}>
            Soyisim:
            <input
              type="text"
              value={newStudentSurname}
              onChange={e => setNewStudentSurname(e.target.value)}
              required
              style={styles.input}
              placeholder="Öğrenci soyadı"
            />
          </label>

          <label style={styles.label}>
            T.C. Kimlik No:
            <input
              type="text"
              value={newStudentTCKimlik}
              onChange={e => setNewStudentTCKimlik(e.target.value)}
              required
              style={styles.input}
              placeholder="11 haneli T.C. Kimlik No"
              maxLength={11}
            />
          </label>

          <label style={styles.label}>
            Email:
            <input
              type="email"
              value={newStudentEmail}
              onChange={e => setNewStudentEmail(e.target.value)}
              required
              style={styles.input}
              placeholder="Öğrencinin e-posta adresi"
            />
          </label>

          <label style={styles.label}>
            Doğum Yeri:
            <input
              type="text"
              value={newStudentBirthplace}
              onChange={e => setNewStudentBirthplace(e.target.value)}
              required
              style={styles.input}
              placeholder="Doğum yeri"
            />
          </label>

          <button type="submit" style={styles.submitButton}>Öğrenciyi Kaydet</button>
        </form>

        {registerError && <p style={{ color: 'red', marginTop: 10 }}>{registerError}</p>}
        {registerSuccess && <p style={{ color: 'green', marginTop: 10 }}>{registerSuccess}</p>}
      </section>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#333',
    maxWidth: 700,
    margin: 'auto',
    backgroundColor: '#fefefe',
    borderRadius: 8,
    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
