import React, { useState, useEffect } from 'react';
import axios from 'axios';

// teacherId prop olarak gelir (backend’den gelen oturum veya kullanıcı ID)
const MessageForm = ({ teacherId }) => {
  const [students, setStudents] = useState([]);
  const [selectedStudentId, setSelectedStudentId] = useState('');
  const [messageText, setMessageText] = useState('');
  const [status, setStatus] = useState('');

  // Öğrencileri getir
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get('http://localhost:8080/api/students');
        setStudents(res.data);
      } catch (err) {
        console.error('Öğrenciler alınamadı', err);
        setStatus('Öğrenciler yüklenirken hata oluştu.');
      }
    };
    fetchStudents();
  }, []);

  // Mesaj gönderme fonksiyonu
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedStudentId || !messageText.trim()) {
      setStatus('Lütfen öğrenci seçip mesaj yazınız.');
      return;
    }

    try {
      await axios.post('http://localhost:8080/api/messages/send-to-student', {
        teacher: { id: Number(teacherId) },       // Teacher ID'yi sayıya çeviriyoruz
        student: { id: Number(selectedStudentId) },  // Student ID'yi sayıya çeviriyoruz
        content: messageText.trim(),
      });
      setStatus('Mesaj gönderildi.');
      setMessageText('');
      setSelectedStudentId('');
    } catch (err) {
      console.error('Mesaj gönderme hatası:', err.response?.data || err.message);
      setStatus('Mesaj gönderilirken hata oluştu.');
    }
  };

  return (
    <div style={{ marginTop: 20 }}>
      <h3>Öğrenciye Mesaj Gönder</h3>
      <form onSubmit={handleSubmit}>
        <select
          value={selectedStudentId}
          onChange={(e) => setSelectedStudentId(e.target.value)}
          style={{ padding: 8, marginRight: 10, minWidth: 200 }}
        >
          <option value="">Öğrenci Seçiniz</option>
          {students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.name} ({student.studentNumber})
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Mesajınızı yazınız"
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          style={{ padding: 8, width: '300px', marginRight: 10 }}
        />
        <button
          type="submit"
          style={{
            padding: '8px 16px',
            backgroundColor: '#2196F3',
            color: 'white',
            border: 'none',
            borderRadius: 4,
            cursor: 'pointer',
          }}
        >
          Gönder
        </button>
      </form>
      {status && <p>{status}</p>}
    </div>
  );
};

export default MessageForm;
