import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ExamScoresPage({ id }) {
  const [examScores, setExamScores] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!id) return;

    const fetchExamScores = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/student/${id}/exams`);
        setExamScores(res.data || []);
        setMessage('');
      } catch (err) {
        setMessage('Sınav notları alınamadı.');
      }
    };

    fetchExamScores();
  }, [id]);

  return (
    <div style={styles.container}>
      <h1>Sınav Notlarınız</h1>
      {message && <p style={{ color: 'red' }}>{message}</p>}
      {examScores.length === 0 ? (
        <p>Sınav notunuz bulunmamaktadır.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Ders</th>
              <th>Not</th>
            </tr>
          </thead>
          <tbody>
            {examScores.map((score, idx) => (
              <tr key={idx}>
                <td>{score.course}</td>
                <td>{score.grade}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '30px auto',
    padding: '20px',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
};
