import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from '../components/TeacherHomePage/TeacherHomePage.module.css';  // CSS modülünü doğru yoldan import ettik

const TeacherHomePage = ({ username }) => {
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const teacherRes = await axios.get(`http://localhost:8080/api/auth/teacher/me?username=${username}`);
        setTeacher(teacherRes.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [username]);

  const handleAddStudent = () => navigate('/ogrenci-ekle');
  const handleShowStudents = () => navigate('/ogrenci-listesi');
  const handleViewMessages = () => navigate('/ogretmen-mesajlar');
  const handleSendMessage = () => navigate('/teacher/send-message', { state: { teacher } });
  const handleSendAnnouncement = () => navigate('/teacher/send-announcement', { state: { teacher } });
  const handleLogout = () => navigate('/teacher-login');

  if (loading) return <div className={styles.loading}>Yükleniyor...</div>;
  if (!teacher) return <div className={styles.error}>Öğretmen bilgisi bulunamadı.</div>;

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h2>Hoşgeldiniz, {teacher.name}</h2>
      </div>

      <div className={styles.buttonGroup}>
        <button className={`${styles.button} ${styles.blue}`} onClick={handleShowStudents}>
          Kayıtlı Öğrenciler
        </button>
        <button className={`${styles.button} ${styles.green}`} onClick={handleAddStudent}>
          Öğrenci Ekle
        </button>
        <button className={`${styles.button} ${styles.orange}`} onClick={handleSendMessage}>
          Mesaj Gönder
        </button>
        <button className={`${styles.button} ${styles.purple}`} onClick={handleSendAnnouncement}>
          Duyuru Yap
        </button>
        <button className={`${styles.button} ${styles.redOrange}`} onClick={handleViewMessages}>
          Mesajları Görüntüle
        </button>
        <button className={`${styles.button} ${styles.red}`} onClick={handleLogout}>
          Çıkış
        </button>
      </div>
    </div>
  );
};

export default TeacherHomePage;
